"use client";
import React, { useState, useEffect, useMemo } from 'react';
import {
  Plus, Circle, CheckCircle2, Star, X, Clock,
  Calendar, Trash2, Edit2, Sparkles
} from 'lucide-react';

const WeightedTodoApp = () => {
  const [tasks, setTasks] = useState([]);
  const [lists, setLists] = useState([
    { id: 'my-tasks', name: 'My Tasks', color: '#1a73e8' },
    { id: 'work', name: 'Work', color: '#0f9d58' },
    { id: 'personal', name: 'Personal', color: '#f4b400' }
  ]);
  const [activeListId, setActiveListId] = useState('starred');
  const [showAddTask, setShowAddTask] = useState(false);
  const [showTaskDetail, setShowTaskDetail] = useState(false);
  const [selectedTask, setSelectedTask] = useState(null);
  const [showCelebration, setShowCelebration] = useState(false);
  const [editingTask, setEditingTask] = useState(null);
  const [showAddList, setShowAddList] = useState(false);
  const [newListName, setNewListName] = useState('');
  const [newListColor, setNewListColor] = useState('#1a73e8');

  const [newTask, setNewTask] = useState({
    title: '',
    date: '',
    time: '',
    points: '',
    notes: ''
  });

  // --- Load tasks/lists from sessionStorage ---
  useEffect(() => {
    const savedTasks = JSON.parse(sessionStorage.getItem('tasks') || '[]');
    const savedLists = JSON.parse(sessionStorage.getItem('lists') || '[]');
    if (savedTasks.length > 0) setTasks(savedTasks);
    if (savedLists.length > 0) setLists(savedLists);
  }, []);

  useEffect(() => {
    sessionStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  useEffect(() => {
    sessionStorage.setItem('lists', JSON.stringify(lists));
  }, [lists]);

  // --- Calculations ---
  const { totalPoints, earnedPoints, progress } = useMemo(() => {
    const today = new Date().toISOString().split('T')[0];
    let filtered = activeListId === 'starred'
      ? tasks.filter(t => t.starred && t.date === today)
      : tasks.filter(t => t.listId === activeListId && t.date === today);

    const total = filtered.reduce((sum, t) => sum + (parseInt(t.points) || 0), 0);
    const earned = filtered.filter(t => t.completed)
      .reduce((sum, t) => sum + (parseInt(t.points) || 0), 0);

    return { totalPoints: total, earnedPoints: earned, progress: total ? Math.round((earned / total) * 100) : 0 };
  }, [tasks, activeListId]);

  useEffect(() => {
    if (progress === 100 && totalPoints > 0) {
      setShowCelebration(true);
      setTimeout(() => setShowCelebration(false), 4000);
    }
  }, [progress, totalPoints]);

  // --- Task Operations ---
  const addTask = () => {
    if (!newTask.title.trim() || !newTask.points) return;
    const task = {
      id: Date.now().toString(),
      listId: activeListId === 'starred' ? 'my-tasks' : activeListId,
      ...newTask,
      completed: false,
      subtasks: [],
      starred: false
    };
    setTasks([...tasks, task]);
    setNewTask({ title: '', date: '', time: '', points: '', notes: '' });
    setShowAddTask(false);
  };

  const updateTask = () => {
    if (!editingTask || !editingTask.title.trim()) return;
    setTasks(tasks.map(t => (t.id === editingTask.id ? editingTask : t)));
    setEditingTask(null);
    setShowTaskDetail(false);
  };

  const toggleTask = id => setTasks(tasks.map(t => t.id === id ? { ...t, completed: !t.completed } : t));

  const deleteTask = id => {
    setTasks(tasks.filter(t => t.id !== id));
    setShowTaskDetail(false);
  };

  const toggleStarred = id => setTasks(tasks.map(t => t.id === id ? { ...t, starred: !t.starred } : t));

  const addSubtask = (taskId, subtaskTitle) => {
    if (!subtaskTitle.trim()) return;
    setTasks(tasks.map(t => t.id === taskId ? {
      ...t,
      subtasks: [...t.subtasks, { id: Date.now().toString(), title: subtaskTitle, completed: false }]
    } : t));
  };

  const toggleSubtask = (taskId, subId) =>
    setTasks(tasks.map(t => t.id === taskId
      ? { ...t, subtasks: t.subtasks.map(s => s.id === subId ? { ...s, completed: !s.completed } : s) }
      : t));

  const addList = () => {
    if (!newListName.trim()) return;
    setLists([...lists, { id: newListName.toLowerCase().replace(/\s+/g, '-'), name: newListName, color: newListColor }]);
    setNewListName('');
    setNewListColor('#1a73e8');
    setShowAddList(false);
  };

  // --- UI Helper Components ---
  const TaskCard = ({ task }) => (
    <div
      className={`group bg-white border border-gray-200 rounded-lg p-3 hover:shadow-md transition-all cursor-pointer ${task.completed ? 'opacity-60' : ''}`}
      onClick={() => { setSelectedTask(task); setShowTaskDetail(true); }}
    >
      <div className="flex items-start gap-2">
        <button onClick={(e) => { e.stopPropagation(); toggleTask(task.id); }}>
          {task.completed ? <CheckCircle2 className="w-4 h-4 text-blue-600" /> : <Circle className="w-4 h-4 text-gray-400" />}
        </button>
        <div className="flex-1">
          <p className={`text-sm font-medium ${task.completed ? 'line-through text-gray-500' : 'text-gray-900'}`}>{task.title}</p>
          {task.points && (
            <span className="inline-flex items-center gap-1 px-2 py-0.5 rounded text-xs font-medium bg-yellow-100 text-yellow-800">
              <Star className="w-3 h-3" />{task.points}
            </span>
          )}
        </div>
        <button onClick={(e) => { e.stopPropagation(); toggleStarred(task.id); }}>
          {task.starred
            ? <Star className="w-4 h-4 text-yellow-500 fill-yellow-500" />
            : <Star className="w-4 h-4 text-gray-300 group-hover:text-gray-400" />}
        </button>
      </div>
    </div>
  );

  const groupedTasks = useMemo(() => {
    const today = new Date().toISOString().split('T')[0];
    const tomorrow = new Date(Date.now() + 86400000).toISOString().split('T')[0];
    let filtered = activeListId === 'starred'
      ? tasks.filter(t => t.starred)
      : tasks.filter(t => t.listId === activeListId);
    return {
      today: filtered.filter(t => t.date === today),
      tomorrow: filtered.filter(t => t.date === tomorrow),
      upcoming: filtered.filter(t => t.date > tomorrow),
      overdue: filtered.filter(t => t.date < today && !t.completed)
    };
  }, [tasks, activeListId]);

  const TaskGroup = ({ title, list }) => (
    list.length > 0 && (
      <div className="mb-6">
        <h3 className="text-xs font-medium text-gray-500 uppercase mb-3">{title}</h3>
        <div className="space-y-2">{list.map(task => <TaskCard key={task.id} task={task} />)}</div>
      </div>
    )
  );

  return (
    <div className="min-h-screen bg-gray-50 p-4 md:p-8">
      {/* Celebration */}
      {showCelebration && (
        <div className="fixed inset-0 flex items-center justify-center z-50">
          <div className="bg-blue-600 text-white px-6 py-3 rounded-full text-lg font-semibold flex items-center gap-2 animate-bounce">
            <Sparkles className="w-5 h-5" /> 100% Complete! Amazing! <Sparkles className="w-5 h-5" />
          </div>
        </div>
      )}

      {/* Header */}
      <h1 className="text-3xl font-semibold mb-6">Weighted To-Do List</h1>

      {/* Lists */}
      <div className="flex gap-2 overflow-x-auto mb-6 pb-2">
        <button
          onClick={() => setActiveListId('starred')}
          className={`px-4 py-2 rounded-lg ${activeListId === 'starred' ? 'bg-blue-600 text-white' : 'bg-gray-100 text-gray-700'}`}
        >
          <Star className="inline w-4 h-4 mr-1" /> Starred
        </button>
        {lists.map(l => (
          <button
            key={l.id}
            onClick={() => setActiveListId(l.id)}
            className={`px-4 py-2 rounded-lg ${activeListId === l.id ? 'bg-blue-600 text-white' : 'bg-gray-100 text-gray-700'}`}
          >
            <span className="inline-block w-2 h-2 rounded-full mr-2" style={{ background: l.color }} /> {l.name}
          </button>
        ))}
        <button onClick={() => setShowAddList(true)} className="px-4 py-2 rounded-lg bg-gray-100 text-gray-700">
          <Plus className="inline w-4 h-4 mr-1" /> New List
        </button>
      </div>

      {/* Progress */}
      <div className="mb-6 bg-white p-4 rounded-xl shadow flex items-center justify-between">
        <div>
          <h2 className="text-lg font-semibold text-gray-900">Today’s Progress</h2>
          <p className="text-gray-500 text-sm">{earnedPoints}/{totalPoints} points • {progress}%</p>
        </div>
        <div className="text-3xl font-bold text-blue-600">{progress}%</div>
      </div>

      {/* Task Groups */}
      <TaskGroup title="Overdue" list={groupedTasks.overdue} />
      <TaskGroup title="Today" list={groupedTasks.today} />
      <TaskGroup title="Tomorrow" list={groupedTasks.tomorrow} />
      <TaskGroup title="Upcoming" list={groupedTasks.upcoming} />

      {/* Add Task Button */}
      <button
        onClick={() => setShowAddTask(true)}
        className="fixed bottom-6 right-6 bg-blue-600 hover:bg-blue-700 text-white rounded-full p-4 shadow-lg"
      >
        <Plus className="w-6 h-6" />
      </button>

      {/* --- Add Task Modal --- */}
      {showAddTask && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
          <div className="bg-white rounded-xl shadow-2xl w-full max-w-md p-6">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-semibold">Add Task</h3>
              <button onClick={() => setShowAddTask(false)}><X /></button>
            </div>
            <input
              type="text"
              placeholder="Task title"
              value={newTask.title}
              onChange={e => setNewTask({ ...newTask, title: e.target.value })}
              className="w-full mb-3 px-3 py-2 border rounded-lg"
            />
            <input
              type="number"
              placeholder="Points"
              value={newTask.points}
              onChange={e => setNewTask({ ...newTask, points: e.target.value })}
              className="w-full mb-3 px-3 py-2 border rounded-lg"
            />
            <textarea
              placeholder="Notes (optional)"
              value={newTask.notes}
              onChange={e => setNewTask({ ...newTask, notes: e.target.value })}
              className="w-full mb-3 px-3 py-2 border rounded-lg"
            />
            <div className="flex justify-end gap-2">
              <button onClick={() => setShowAddTask(false)} className="px-4 py-2 border rounded-lg">Cancel</button>
              <button onClick={addTask} className="px-4 py-2 bg-blue-600 text-white rounded-lg">Add</button>
            </div>
          </div>
        </div>
      )}

      {/* --- Add List Modal --- */}
      {showAddList && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
          <div className="bg-white rounded-xl shadow-2xl w-full max-w-md p-6">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-semibold">Add List</h3>
              <button onClick={() => setShowAddList(false)}><X /></button>
            </div>
            <input
              type="text"
              placeholder="List name"
              value={newListName}
              onChange={e => setNewListName(e.target.value)}
              className="w-full mb-3 px-3 py-2 border rounded-lg"
            />
            <div className="flex gap-2 mb-4">
              {['#1a73e8', '#0f9d58', '#f4b400', '#db4437', '#ab47bc', '#00acc1'].map(color => (
                <button
                  key={color}
                  onClick={() => setNewListColor(color)}
                  className={`w-8 h-8 rounded-full ${newListColor === color ? 'ring-2 ring-blue-500' : ''}`}
                  style={{ backgroundColor: color }}
                />
              ))}
            </div>
            <div className="flex justify-end gap-2">
              <button onClick={() => setShowAddList(false)} className="px-4 py-2 border rounded-lg">Cancel</button>
              <button onClick={addList} className="px-4 py-2 bg-blue-600 text-white rounded-lg">Add</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default WeightedTodoApp;
